import React, { Component } from 'react';
import "./styles/Page.css"
import me from "../images/mee.png"
import milo from "../images/milo.png"

  const images = {
    me: "../images/mee.png",
    milo: "../images/milo.png"
  }

class About extends Component {
  state = {
    me: true
  }

  switchPics = () => {
    this.setState(state => ({ me: !state.me }))
  }

  getImageName = () => this.state.me ? 'me' : 'milo'

  render() {
    const imageName = this.getImageName();
    return (
      <div className="Page">

      	<div className="banner-about">
      		<div className="title">
				Computer Science Student
      		</div>
      		<div className="subtitle">
				TV show enthusiast
      		</div>
      	</div>

      	<div className="page-body">
    			<div className="body-title">	
    				Who am I?	
    			</div>
          <p className="bio"><img style={{maxWidth: '50px'}} src={images[imageName]} onClick={this.switchPics} alt=""></img>
            Samar El-Houssami. That's me in the picture. I'm a Computer Science
            student at the University of Windsor. I watch a lot of TV (mostly comedies) 
            and I'm also a fan of the Marvel movies. I have a dog (his name is Milo) and if you
            want to see a picture of him, <b>click on my picture</b>.
          </p>
      	</div>
      </div>
    );
  }
}

export default About;
