import React, { Component } from 'react';
import { Button, ButtonToolbar, ToggleButtonGroup } from 'react-bootstrap'
import { LinkContainer } from 'react-router-bootstrap'
import { NavLink } from "react-router-dom";
import logo from '../images/logo.png';
import './styles/DashBoard.css';

class DashBoard extends Component {
    constructor(props) {
    super(props);

    this.state = {
    	background: "DashBoard-home"
    };
  }

  changeState(props) {
  	this.setState({ background: "DashBoard-"+props });
  }	

  render() {
    return (
      <div className="DashBoard">
      	<span>
          <img src={logo} className="App-logo" alt="logo" />
        </span>

		<div className="navigation">
			<LinkContainer to="/Home">
   				<p className="dash-btn" tabIndex="1">
    				HOME
    			</p>
			</LinkContainer>
			<LinkContainer to="/About">
   				<p className="dash-btn" tabIndex="2">
    				ABOUT
    			</p>
			</LinkContainer>
			<LinkContainer to="/Contact" tabIndex="3">
   				<p className="dash-btn" onClick={this.changeState("contact").bind(this)}>
    				CONTACT
    			</p>
			</LinkContainer>
		</div>

			{/*<ButtonToolbar className="btn-group">
				<LinkContainer to="/Home">
	   				<Button variant="outline-dark" className="dash-btn">
	    				HOME
	    			</Button>
				</LinkContainer>
				<LinkContainer to="/About">
	   				<Button variant="outline-dark" className="dash-btn">
	    				ABOUT
	    			</Button>
				</LinkContainer>
				<LinkContainer to="/Contact">
	   				<Button variant="outline-dark" className="dash-btn">
	    				CONTACT
	    			</Button>
				</LinkContainer>
			</ButtonToolbar>*/}
      </div>
    );
  }
}

export default DashBoard;
